package test;

public class T01 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("안녕하세요!");
		
		int a = 10;
		int b = 3;
		int c1 = a + b;
		int c2 = a - b;
		int c3 = a * b;
		int c4 = a / b;
		
		System.out.println(a + " + " + b + " = " + c1);
		System.out.println(a + " * " + b + " = " + c3);
		System.out.println(a + " - " + b + " = " + c2);
		System.out.println(a + " / " + b + " = " + c4);

	}


}
